// page init
jQuery(function(){
	initFixedScrollBlock();
	initTabs();
	initTouchNav();
	initLightbox();
	initMobileNav();
	initSlideShow();
	initOpenClose();
	initDropDownClasses();
	initCheckedClasses();
	initFitVids();
	jQuery('input, textarea').placeholder();
});

// initialize fixed blocks on scroll
function initFixedScrollBlock() {
	jQuery('#wrapper').fixedScrollBlock({
		slideBlock: '#header',
		positionType: 'fixed'
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('.slideshow').fadeGallery({
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		event: 'click',
		disableWhileAnimating: true,
		disableFadeIE: true,
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 4000,
		animSpeed: 500
	});
}

// open-close init
function initOpenClose() {
	jQuery('.open-close').openClose({
		hideOnClickOutside: true,
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
	/*jQuery('.open-close-open').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide',
		//passive: true
	});*/
	
	jQuery('.open-close-select').openClose({
		hideOnClickOutside: true,
		activeClass: 'active',
		opener: '.opener-select',
		slider: '.slide-select',
		animSpeed: 400,
		effect: 'none'
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[rel*="lightbox"]').fancybox({
		helpers: {
			overlay: {
				css: {
					background: 'rgba(0, 0, 0, 0.65)'
				}
			}
		},
		afterLoad: function(current, previous) {
			// handle custom close button in inline modal
			if(current.href.indexOf('#') === 0) {
				jQuery(current.href).find('a.close').off('click.fb').on('click.fb', function(e){
					e.preventDefault();
					jQuery.fancybox.close();
				});
			}
		}
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		menuDrop: '#nav'
	});
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'form-active',
		menuOpener: '.form-opener',
		menuDrop: '.form-search-slider'
	});
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'topbar-active',
		menuOpener: '.topbar-opener',
		menuDrop: '.top-bar'
	});
	jQuery('html').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'body-lock',
		menuOpener: '.main-panel .opener',
		menuDrop: '.main-panel'
	});
}

// content tabs init
function initTabs() {
	jQuery('.tabset').tabset({
		tabLinks: 'a',
		addToParent: true,
		defaultTab: false
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('#nav').each(function(){
		new TouchNav({
			navBlock: this,
			menuDrop: '.drop'
		});
	});
}

// add classes if item has dropdown
function initDropDownClasses() {
	jQuery('#nav li').each(function() {
		var item = jQuery(this);
		var drop = item.find('ul');
		var link = item.find('a').eq(0);
		if(drop.length) {
			item.addClass('has-drop-down');
			if(link.length) link.addClass('has-drop-down-a');
		}
	});
}

;(function(w) {
	w.addEventListener('load', function() {
		var loader = document.querySelector('html.loader');

		if (loader) {
			loader.classList.add('loaded');
		}
	});
}(window));

// handle flexible video size
function initFitVids() {
	jQuery('#wrapper').fitVids();
}

// checked classes when element active
function initCheckedClasses() {
	var checkedClass = 'input-checked', parentCheckedClass = 'input-checked-parent';
	var pairs = [];
	jQuery('label[for]').each(function(index, label) {
		var input = jQuery('#'+label.htmlFor);
		label = jQuery(label);

		// click handler
		if(input.length) {
			pairs.push({input:input,label:label});
			input.bind('click change', function() {
				if(input.is(':radio')) {
					jQuery.each(pairs, function(index, pair) {
						refreshState(pair.input, pair.label);
					});
				} else {
					refreshState(input, label);
				}
			});
			refreshState(input, label);
		}
	});

	// refresh classes
	function refreshState(input, label) {
		if(input.is(':checked')) {
			input.parent().addClass(parentCheckedClass);
			label.addClass(checkedClass);
		} else {
			input.parent().removeClass(parentCheckedClass);
			label.removeClass(checkedClass);
		}
	}
}

